import {
  useState,
  useEffect
} from "react";
import {getAllStudents} from "./client";
import './App.css';

function App() {

  const [students, setStudents] = useState([]);

  const fetchStudents = () => {
    getAllStudents()
        .then(res => res.json())
        .then(data => {
          console.log(data);
          setStudents(data);
        })
  }
  useEffect(() => {
    console.log("Component is mounted");
    fetchStudents();
  },[]);

  if(students.length <= 0){
    return "No Data";
  }


}

export default App;
